    <!-- inline scripts related to this page -->
    <script type=""text/javascript"">
        function UpdateData(action, id) {
            if (action === 'create') {
                $(""#frmUpdateLabel"").html(""Create new role"");
                $(""#frmUpdate form"").attr(""action"", ""/RolesAdmin/Create"");
                $(""#txtName"").val("""");
