            else {
                $(""#frmUpdate form"").attr(""action"", ""/RolesAdmin/Update"");
                $(""#frmUpdateLabel"").html(""Edit role"");
                $(""#txtName"").val($(""#hdName_"" + id).val());
            }
            $(""#CurrentID"").val(id);

            $('#frmUpdate').modal('show');
        }
    </script>
");
